// see https://usefulangle.com/post/30/javascript-get-date-time-with-offset-hours-minutes
var offsetInMin = new Date().getTimezoneOffset();
var hrsOffset = Math.abs(offsetInMin/60);
var minOffset = Math.abs(offsetInMin%60);
var hhOffset = ("00" + hrsOffset).slice(-2);
var mmOffset = ("00" + minOffset).slice(-2);

var offsetIso8601 = undefined;
if (offsetInMin < 0)
    offsetIso8601 = "+" + hhOffset + mmOffset;
else if (offsetInMin > 0)
    offsetIso8601 = "-" + hhOffset + mmOffset;
else if (offsetInMin === 0)
    offsetIso8601 = "Z";

msg.timezone = offsetIso8601;
return msg;
